sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var ga = new GlideAjax('x_660049_roop_po_0.returnChemicalDescription');
    ga.addParam('sysparm_name', 'returnChemicalDescription');
    ga.addParam('sysparm_fmb', newValue);
    ga.getXML(getData);

    function getData(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        g_form.setValue('chemical_description', answer);
    }

}
