sys_script_include.script:
var returnChemicalDescription = Class.create();
returnChemicalDescription.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    returnChemicalDescription: function() {

        var fmb = this.getParameter('sysparm_fmb');
        var cmb = [];
        var des = [];
        var gr = new GlideRecord('x_660049_roop_po_0_fmb_batch');
        gr.addQuery('sys_id', fmb);
        gr.query();
        if (gr.next()) {
            cmb = gr.chemical_used;
        }
        var array = cmb.split(",");
        for (var i = 0; i < array.length; i++) {
            var gr1 = new GlideRecord('x_660049_roop_po_0_chemical_mater_table');
            gr1.addQuery('sys_id', array[i]);
            gr1.query();
            if (gr1.next()) {
                des.push(gr1.chemical_item_code_description);
            }
        }

        return des.toString();

    },
    type: 'returnChemicalDescription'
});
