sys_script_include.script:
var CreateNewRecord = Class.create();
CreateNewRecord.prototype = {
    initialize: function() {},

    createRecord: function(table, fieldValues) {
        var gr = new GlideRecord(table);
        gr.initialize();
        for (var fieldName in fieldValues) {
            if (fieldValues.hasOwnProperty(fieldName)) {
                gr[fieldName] = fieldValues[fieldName];
            }
        }
        gr.insert();
        return gr;
    },

    type: 'CreateNewRecord'
};
