sys_script_include.script:
var returnCMBDescription = Class.create();
returnCMBDescription.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

            returnCMBDescription: function() {

                var fmb = this.getParameter('sysparm_fmb');
                var cmb = [];
				var des=[];
                var gr = new GlideRecord('x_660049_roop_po_0_fmb_batch');
                gr.addQuery('sys_id', fmb);
                gr.query();
                if (gr.next()) {
                    cmb = gr.cmb_used;
                }
                var array = cmb.split(",");
                for (var i = 0; i < array.length; i++) {
					var gr1= new GlideRecord('x_660049_roop_po_0_cmb_master_table');
				    gr1.addQuery('sys_id',array[i]);
					gr1.query();
					if(gr1.next()){
						gs.info('VK1'+ gr1.cmb_description);
                     des.push(gr1.cmb_description);
					}
				}
				gs.info('X2',des);
				return des.toString();

                },

                type: 'returnCMBDescription'
            });
